package Loops;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int j=20;
		
		int i=20;
		
		do {
			
			System.out.println(j++);
			
		}while(j<=30);
		
		
		System.out.println("\n\nEnters only once here");
		do {
			
			System.out.println(i++);
			
		}while(i>30);
		
		
	}

}
